SELECT analyses.id, nageurs.id, nageurs.prenom, nageurs.nom, nageurs.sexe, nageurs.anneeDeNaissance, nageurs.nationalite, nageurs.club, courses.dateCourse, competitions.nom, distances.libelle, styles.libelle, rounds.libelle, analyses.tpsDebutCourse, analyses.tpsFinal, analyses.tpsReaction, analyses.tpsDecollagePieds, analyses.tpsVol, analyses.couloir
FROM rounds INNER JOIN (styles INNER JOIN (distances INNER JOIN (competitions INNER JOIN (courses INNER JOIN (((nageurs INNER JOIN participants ON nageurs.id = participants.idNageur) INNER JOIN figurants ON participants.id = figurants.idParticipant) INNER JOIN analyses ON figurants.id = analyses.idFigurant) ON courses.id = participants.idCourse) ON competitions.id = courses.idCompetition) ON distances.id = courses.distance) ON styles.id = courses.style) ON rounds.id = courses.round;
