SELECT analyses.id, nageurs.id, nageurs.prenom, nageurs.nom, nageurs.sexe, nageurs.anneeDeNaissance, nageurs.nationalite, nageurs.club, courses.id, courses.dateCourse, competitions.nom, courses.longueurBassin, distances.libelle, styles.libelle, rounds.libelle, analyses.tpsDebutCourse, analyses.tpsFinal, analyses.tpsReaction, analyses.tpsDecollagePieds, analyses.tpsVol, analyses.couloir
FROM styles INNER JOIN (rounds INNER JOIN ((nageurs INNER JOIN (distances INNER JOIN ((competitions INNER JOIN courses ON competitions.id = courses.idCompetition) INNER JOIN participants ON courses.id = participants.idCourse) ON distances.id = courses.distance) ON nageurs.id = participants.idNageur) INNER JOIN (figurants INNER JOIN analyses ON figurants.id = analyses.idFigurant) ON participants.id = figurants.idParticipant) ON rounds.id = courses.round) ON styles.id = courses.style;
